from io import BytesIO

from pyrogram.api.core import *


class MessageActionGameScore(Object):
    """
    Attributes:
        ID (:obj:`int`): ``0x92a72876``

    Args:
        game_id: :obj:`int`
        score: :obj:`int`
    """
    ID = 0x92a72876

    def __init__(self, game_id, score):
        self.game_id = game_id  # long
        self.score = score  # int

    @staticmethod
    def read(b: BytesIO, *args) -> "MessageActionGameScore":
        # No flags
        
        game_id = Long.read(b)
        
        score = Int.read(b)
        
        return MessageActionGameScore(game_id, score)

    def write(self) -> bytes:
        b = BytesIO()
        b.write(Int(self.ID, False))

        # No flags
        
        b.write(Long(self.game_id))
        
        b.write(Int(self.score))
        
        return b.getvalue()
